properties.query:
let Mango = view () {
    workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").OfficeActivity
	| where (Operation=="UpdateInboxRules" or Operation=="Forward") | where OperationProperties[6].Value!="[]" 
	| where OperationProperties contains "@"
	| where parse_json(tostring(OperationProperties[6].Value))[0].ActionType == "Forward"
	| where parse_json(tostring(parse_json(tostring(OperationProperties[6].Value))[0].Recipients))[0] contains "@mango.com"
	| summarize ["Número de cuentas internas a las que se hizo redirección"] = dcount(tostring(parse_json(tostring(parse_json(tostring(OperationProperties[6].Value))[0].Recipients))[0])), ["Cuentas internas a las que se hizo redirección"] = make_set(tostring(parse_json(tostring(parse_json(tostring(OperationProperties[6].Value))[0].Recipients))[0])) by UserId
	| where ["Número de cuentas internas a las que se hizo redirección"] >= 2
	};
union withsource="TenantDetection"  Mango
