let max_delete = 3;
let Mango = view () {
    workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").OfficeActivity
	| where OfficeWorkload =~ "MicrosoftTeams"
	| where Operation =~ "TeamDeleted" and UserType != "Application"
	| summarize ['Number of Teams Deleted'] = dcount(TeamName), ["Teams Deleted"] = make_set(TeamName) by UserId
	| where ['Number of Teams Deleted'] > max_delete
	};
union withsource="TenantDetection"  Mango

let Mango = view () {
    workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").OfficeActivity
    | where (Operation == "SharingInvitationCreated" or Operation == "AnonymousLinkCreated")
    | summarize count() by UserId
    | where count_ >= 3
    | extend AccountCustomEntity = UserId
};
union withsource="TenantDetection"  Mango

let Mango = view () {
workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").OfficeActivity
| extend PolicyAction="" | extend Verdict=""
| where Operation=="TIMailData" and PolicyAction=="NoAction" and Verdict=="Phish"
};
union withsource="TenantDetection"  Mango

let Mango = view () {
workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").OfficeActivity
| where (Operation=="UpdateInboxRules" or Operation=="Forward") | where OperationProperties[6].Value!="[]" 
| where parse_json(tostring(OperationProperties[6].Value))[0].ActionType == "Forward"
| where parse_json(tostring(parse_json(tostring(OperationProperties[6].Value))[0].Recipients))[0] !contains "@mango.com" 
};
union withsource="TenantDetection"  Mango

let Mango = view () {
workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").SecurityEvent 
| where (EventID == 4720 and (TargetUserName endswith ".Adm" or TargetUserName endswith ".adm" or TargetSid endswith "-500"))
| extend AccountCustomEntity = TargetUserName
| extend HostCustomEntity = Computer
};
union withsource="TenantDetection"  Mango

