let Mango = view () {
    let VIPList = Mango_VIP_Users | project samAccountName;
    workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").SecurityEvent
    | where EventID == 4768
    // Extract TargetUserName from RAW data because sometimes the field "Account" comes empty
    | parse EventData with *'"TargetUserName">' TargetUserName '</Data>'*
    // If TargetUserName contains the domain (like CLIENT\user_name), we'll keep only the username to be able to check it against VIP lookup
    | extend samAccountName = split(TargetUserName,'\\')
    | extend samAccountName = iff(array_length(samAccountName) > 1, samAccountName[1], samAccountName[0])
    // We do the same removing @CLIENT.DOMAIN
    | extend samAccountName = split(samAccountName,'@')[0]
    | where samAccountName !in (VIPList) and isnotempty(samAccountName)
    | where Status != "0x0"
    | summarize occurrences = count() by tostring(samAccountName)
    | where occurrences >= 100
};
union withsource="TenantDetection" Mango

