    workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").SecurityEvent
    | where EventID in (4727, 4731, 4744, 4749, 4754, 4759)
    // Extract SubjectUserName from RAW data because sometimes the field "SubjectUserName" comes empty
    | parse EventData with *'"SubjectUserName">' SubjectUserName '</Data>'*
