let Mango = view () {
    let Blacklist = toscalar(Mango_Blacklist_ExecutableName | summarize make_set(MatchString));
    let Events = workspace("4d082081-7c2a-4f57-b848-bd64e5c72859").SecurityEvent
    | where EventID == 4625 and (isnotnull(Process) and isnotempty(Process) and Process != "-");
    Events
    | mv-apply BlacklistMatch = Blacklist on (
            where Process contains BlacklistMatch
    )
    | summarize occurrences = count() by tostring(Account), Process, tostring(BlacklistMatch)
};
